package javaExamples;

public class Else_If1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("***AGE STATUS***");
		
		int age=10;
		
		if(age>10)
		{
			System.out.println("A BOY AGE" +":" +age);
		}
		
		else if(age<10)
		{
			System.out.println("A BOY AGE" +":" +age);
		}
		
		else if(age>=10)
		{
			System.out.println("A BOY AGE" +":" +age);
		}
		
		else if(age==10)
		{
			System.out.println("A BOY AGE" +":" +age);
		}
		
		else
		{
			System.out.println("a boy age cant describe");
		}

	}

}
