package javaExamples;

public class If_Else3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		System.out.println("DIVISIBLE BY ZERO");
		
		int a=15;
		int b=a/0;
		
		if(b==0)
		{
			System.out.println("15  IS DIVISIBLE BY ZERO ");
		}
		else
		{
			System.out.println("15 IS NOT DIVISIBLE BY ZERO");
		}

	}

}
