package javaExamples;

public class While_loop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("PRINT STRING 10 TIMES");
		
	    int a=0;
		
		while(a<=10)
		{
			System.out.println("String"  +a  +":JAVA IS VERY EASY" );
			a++;
		}

	}

}
