package vedika;

public class Elseif_cond {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a=50;
		if(a==50)
		{
			System.out.println("a VALUE IS 50");
		}
		
		else if(a>40)
		{
			System.out.println("a VALUE IS GREATER THAN 40");
		}
		else if(a>=50)
		{
			System.out.println("a VALUE IS LESS THAN 40");
		}
		else
		{
			System.out.println("ELSE STATEMENT EXECUTED");
		}
	}

}
